mapper.select:
SELECT id, username, hash, salt, role
FROM users
WHERE username = #{username}

SELECT id, username, hash, salt, role
FROM users
WHERE id = #{id}

mapper.update:
UPDATE users
SET username = #{username},
    hash     = #{hash},
    salt     = #{salt},
    role     = #{role}
WHERE id = #{id}

mapper.select:
SELECT username
FROM users
WHERE id = #{id}
